impl Process { /// 添加一个新的对象句柄 pub fn add_handle(&self, handle: Handle) -> HandleValue { let mut inner = self.inner.lock(); let value = (0 as HandleValue..) .find(|idx| !inner.handles.contains_key(idx)) .unwrap(); inner.handles.insert(value, handle); value } /// 删除一个对象句柄 pub fn remove_handle(&self, handle_value: HandleValue) { self.inner.lock().handles.remove(&handle_value); }
}

